import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Card from '../components/Card';
import { supabase } from '../client';
import './readposts.css'; // Import the readposts.css file

const ReadPosts = () => {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchPosts = async () => {
            const { data } = await supabase.from('Posts').select('id, title, author, description, image, created_at');
            setPosts(data);
            setFilteredPosts(data);
        };

        fetchPosts();
    }, []);

    const handleSearch = (event) => {
        const query = event.target.value.toLowerCase();
        setSearchQuery(query);

        if (query.trim() === '') {
            setFilteredPosts(posts);
        } else {
            const filtered = posts.filter(post => post.title.toLowerCase().includes(query));
            setFilteredPosts(filtered);
        }
    };

    return (
        <div className="ReadPosts"> {/* Apply the ReadPosts class here */}
            <input
                type="text"
                placeholder="Search by title..."
                value={searchQuery}
                onChange={handleSearch}
            />

            {filteredPosts.map((post) => (
                <Link key={post.id} to={`/post/${post.id}`}>
                    <Card
                        id={post.id}
                        title={post.title}
                        author={post.author}
                        description={post.description}
                        image={post.image}
                        created_at={post.created_at}
                    />
                </Link>
            ))}
        </div>
    );
};

export default ReadPosts;
