import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const DetailPost = ({ data }) => {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Find the post with matching id from data array
    const foundPost = data.find(post => post.id === id);
    if (foundPost) {
      setPost(foundPost);
      setLoading(false); // Set loading to false once post is found
    } else {
      setLoading(false); // Set loading to false even if post is not found
    }
  }, [data, id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!post) {
    return <div>Post not found!</div>;
  }

  return (
    <div>
      <h2>{post.title}</h2>
      <p>{post.author}</p>
      <p>{post.description}</p>
      <p>{post.created_at}</p>
    </div>
  );
};

export default DetailPost;
