import React, { useState, useEffect } from 'react';
import './Card.css';
import more from './more.png';
import { Link } from 'react-router-dom';
import { supabase } from '../client'; // Import the supabase client

const Card = ({ id, title, author, image, description, created_at, likes = 0 }) => {
    const [count, setCount] = useState(likes); // Initialize count with likes if available

    useEffect(() => {
        // Fetch the current count from local storage when component mounts
        const storedCount = localStorage.getItem(`betCount_${id}`);
        if (storedCount) {
            setCount(parseInt(storedCount));
        }
    }, [id]);

    const updateCount = async (event) => {
        event.preventDefault(); // Prevent default button behavior
        const updatedCount = count + 1;
        setCount(updatedCount);

        // Update the bet count in the database
        try {
            const { error } = await supabase
                .from('Posts')
                .update({ betCount: updatedCount }) // Update the bet count
                .eq('id', id); // Update the post with the matching id

            if (error) {
                throw error;
            }

            // Store the updated count in local storage
            localStorage.setItem(`betCount_${id}`, updatedCount.toString());
        } catch (error) {
            console.error('Error updating bet count:', error.message);
            // Rollback the count if there's an error
            setCount(count);
        }
    };

    return (
        <div className="Card">
            <Link to={`/edit/${id}`}> {/* Update link to edit */}
                <img className="moreButton" alt="edit button" src={more} />
            </Link>
            <h2 className="title">{title}</h2>
            <h3 className="author">{"by " + author}</h3>
            {image && <img className="image" src={image} alt="post" />}
            <p className="description">{description}</p>
            {created_at && (
                <p className="timestamp">Created at: {new Date(created_at).toLocaleString()}</p>
            )}
            <button className="betButton" onClick={updateCount}>
                &#128077; Likes: {count}
            </button>
        </div>
    );
};

export default Card;
