import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Card from '../components/Card';
import { supabase } from '../client';

const ReadPosts = () => {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [applyFilter, setApplyFilter] = useState(false);
    const [sortCriteria, setSortCriteria] = useState('created_at');

    useEffect(() => {
        const fetchPosts = async () => {
            const { data } = await supabase.from('Posts').select('id, title, author, description, image, created_at, betCount');
            setPosts(data);
            setFilteredPosts(data);
        };

        fetchPosts();
    }, []);

    useEffect(() => {
        applyFilters();
    }, [searchQuery, applyFilter, sortCriteria]);

    const applyFilters = () => {
        let filtered = [...posts];

        if (applyFilter) {
            filtered = filtered.filter(post => post.title.toLowerCase().includes(searchQuery.toLowerCase()));
        }

        if (sortCriteria === 'created_at') {
            filtered.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
        } else if (sortCriteria === 'likes') {
            filtered.sort((a, b) => b.betCount - a.betCount);
        }

        setFilteredPosts(filtered);
    };

    const handleSearch = (event) => {
        const query = event.target.value;
        setSearchQuery(query);
    };

    const handleApplyFilterChange = (event) => {
        setApplyFilter(event.target.checked);
    };

    const handleSortChange = (event) => {
        setSortCriteria(event.target.value);
    };

    return (
        <div className="container mt-4">
            <div className="row mb-4">
                <div className="col-md-6">
                    <div className="form-check">
                        <input
                            type="checkbox"
                            className="form-check-input"
                            checked={applyFilter}
                            onChange={handleApplyFilterChange}
                        />
                        <label className="form-check-label">Apply Filter</label>
                    </div>
                </div>
                <div className="col-md-6">
                    <div className="form-group">
                        <select className="form-select" value={sortCriteria} onChange={handleSortChange}>
                            <option value="created_at">Sort by Created Time</option>
                            <option value="likes">Sort by Likes</option>
                        </select>
                    </div>
                </div>
            </div>
            <div className="form-group mb-4">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search by title..."
                    value={searchQuery}
                    onChange={handleSearch}
                    disabled={!applyFilter}
                />
            </div>

            <div className="row">
                {filteredPosts.map((post) => (
                    <div className="col-md-4 mb-4" key={post.id}>
                        <Link to={`/post/${post.id}`} className="text-decoration-none text-dark">
                            <Card
                                id={post.id}
                                title={post.title}
                                author={post.author}
                                description={post.description}
                                image={post.image}
                                created_at={post.created_at}
                            />
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ReadPosts;
