          if (internals.debug) {
            try {
              const result = await globalThis.navigator.locks.query()

              console.log(
                '@supabase/gotrue-js: Navigator LockManager state',
                JSON.stringify(result, null, '  ')
              )
            } catch (e: any) {
              console.warn(
                '@supabase/gotrue-js: Error when querying Navigator LockManager state',
                e
              )
            }
          }

