import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Card from '../components/Card';
import { supabase } from '../client';
import './ReadPosts.css'; // Ensure you have this file for custom styles

const ReadPosts = () => {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [applyFilter, setApplyFilter] = useState(false);
    const [sortCriteria, setSortCriteria] = useState('created_at');

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const { data, error } = await supabase.from('Posts').select('id, title, author, description, image, created_at, betCount');
                if (error) throw error;
                console.log('Fetched posts:', data);
                setPosts(data);
                setFilteredPosts(data);
            } catch (error) {
                console.error('Error fetching posts:', error.message);
            }
        };

        fetchPosts();
    }, []);

    useEffect(() => {
        applyFilters();
    }, [searchQuery, applyFilter, sortCriteria]);

    const applyFilters = () => {
        let filtered = [...posts];

        if (applyFilter) {
            filtered = filtered.filter(post => post.title.toLowerCase().includes(searchQuery.toLowerCase()));
        }

        console.log('Sorting criteria:', sortCriteria);

        if (sortCriteria === 'created_at') {
            filtered.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
        } else if (sortCriteria === 'likes') {
            filtered.sort((a, b) => b.betCount - a.betCount);
        }

        console.log('Filtered and sorted posts:', filtered);

        setFilteredPosts(filtered);
    };

    const handleSearch = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleApplyFilterChange = (event) => {
        setApplyFilter(event.target.checked);
    };

    const handleSortChange = (event) => {
        setSortCriteria(event.target.value);
    };

    return (
        <div className="ReadPosts">
            <div className="filter-options">
                <label>
                    <input type="checkbox" checked={applyFilter} onChange={handleApplyFilterChange} />
                    Apply Filter
                </label>
                <select value={sortCriteria} onChange={handleSortChange}>
                    <option value="created_at">Sort by Created Time</option>
                    <option value="likes">Sort by Likes</option>
                </select>
            </div>
            <input
                type="text"
                placeholder="Search by title..."
                value={searchQuery}
                onChange={handleSearch}
                disabled={!applyFilter}
            />
            <div className="cards-container">
                {filteredPosts.map((post) => (
                    <Link key={post.id} to={`/post/${post.id}`}>
                        <Card
                            id={post.id}
                            title={post.title}
                            author={post.author}
                            description={post.description}
                            image={post.image}
                            created_at={post.created_at}
                            likes={post.betCount} // Pass betCount as likes
                        />
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default ReadPosts;
