import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../client';
import './Card.css';
import more from '../assets/more.png'; // Make sure this path matches your directory structure


const Card = (props) => {
    const [count, setCount] = useState(props.likes || 0);

    useEffect(() => {
        const storedCount = localStorage.getItem(`betCount_${props.id}`);
        if (storedCount) {
            setCount(parseInt(storedCount));
        }
    }, [props.id]);

    const updateCount = async (event) => {
        event.preventDefault();
        const updatedCount = count + 1;
        setCount(updatedCount);

        try {
            const { error } = await supabase
                .from('Posts')
                .update({ betCount: updatedCount })
                .eq('id', props.id);

            if (error) {
                throw error;
            }

            localStorage.setItem(`betCount_${props.id}`, updatedCount.toString());
        } catch (error) {
            console.error('Error updating bet count:', error.message);
            setCount(count);
        }
    };

    return (
        <div className="card mb-4 shadow-sm">
            <Link to={`/edit/${props.id}`}>
                <i className="bi bi-pencil btn btn-light float-end"></i> {/* Bootstrap icon */}
            </Link>
            <div className="card-body">
                <h5 className="card-title">{props.title}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{"by " + props.author}</h6>
                {props.image && <img className="card-img-top" src={props.image} alt="post" />}
                <p className="card-text">{props.description}</p>
                {props.created_at && (
                    <p className="text-muted small">Created at: {new Date(props.created_at).toLocaleString()}</p>
                )}
                <button className="btn btn-primary" onClick={updateCount}>
                    üëç Likes: {count}
                </button>
            </div>
        </div>
    );
};

export default Card;
