import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Card from '../components/Card';
import { supabase } from '../client';

const ReadPosts = () => {
    const [posts, setPosts] = useState([]);
    const [filteredPosts, setFilteredPosts] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortCriteria, setSortCriteria] = useState('created_at');

    useEffect(() => {
        const fetchPosts = async () => {
            const { data } = await supabase.from('Posts').select('id, title, author, description, image, created_at, betCount');
            setPosts(data);
            setFilteredPosts(data);
        };

        fetchPosts();
    }, []);

    useEffect(() => {
        applyFilters();
    }, [searchQuery, sortCriteria]);

    const applyFilters = () => {
        let filtered = [...posts];

        if (searchQuery) {
            filtered = filtered.filter(post => post.title.toLowerCase().includes(searchQuery.toLowerCase()));
        }

        if (sortCriteria === 'created_at') {
            filtered.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
        } else if (sortCriteria === 'likes') {
            filtered.sort((a, b) => b.betCount - a.betCount);
        }

        setFilteredPosts(filtered);
    };

    const handleSearch = (event) => {
        const query = event.target.value;
        setSearchQuery(query);
    };

    const handleSortChange = (event) => {
        setSortCriteria(event.target.value);
    };

    return (
        <div className="ReadPosts container mt-4">
            <div className="filter-options mb-4">
                <div className="d-flex justify-content-between">
                    <input
                        type="text"
                        className="form-control w-50"
                        placeholder="Search by title..."
                        value={searchQuery}
                        onChange={handleSearch}
                    />
                    <select className="form-select w-25" value={sortCriteria} onChange={handleSortChange}>
                        <option value="created_at">Sort by Created Time</option>
                        <option value="likes">Sort by Likes</option>
                    </select>
                </div>
            </div>

            <div className="row">
                {filteredPosts.map((post) => (
                    <div key={post.id} className="col-md-4 mb-3">
                        <Link to={`/post/${post.id}`}>
                            <Card
                                id={post.id}
                                title={post.title}
                                author={post.author}
                                description={post.description}
                                image={post.image}
                                created_at={post.created_at}
                            />
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ReadPosts;
