import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Card from '../components/Card';
import { supabase } from '../client';

const PostPage = () => {
    const { postId } = useParams();
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [commentText, setCommentText] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        console.log('Post ID:', postId); // Debugging statement
        if (!postId || isNaN(parseInt(postId))) {
            setError('Invalid post ID');
            return;
        }

        const fetchPost = async () => {
            try {
                const { data } = await supabase.from('Posts').select('*').eq('id', postId).single();
                setPost(data);
            } catch (error) {
                setError('Error fetching post data');
            }
        };

        fetchPost();
    }, [postId]);

    useEffect(() => {
        const fetchComments = async () => {
            try {
                if (postId) {
                    const { data } = await supabase.from('Comments').select('*').eq('post_id', postId);
                    setComments(data || []);
                }
            } catch (error) {
                setError('Error fetching comments data');
            }
        };

        fetchComments();
    }, [postId]);

    const handleSubmitComment = async (event) => {
        event.preventDefault();
        if (!commentText.trim()) return;

        try {
            await supabase.from('Comments').insert([{ post_id: postId, text: commentText }]);
            setCommentText('');
            // Refresh comments
            const { data } = await supabase.from('Comments').select('*').eq('post_id', postId);
            setComments(data || []);
        } catch (error) {
            setError('Error adding comment');
        }
    };

    return (
        <div className="PostPage">
            <div className="post-details">
                {post ? (
                    <Card
                        id={post.id}
                        title={post.title}
                        author={post.author}
                        description={post.description}
                        image={post.image}
                        created_at={post.created_at}
                    />
                ) : (
                    <p>No post found</p>
                )}
            </div>

            <div className="comments">
                <h3>Comments</h3>
                <ul>
                    {comments.map(comment => (
                        <li key={comment.id}>{comment.text}</li>
                    ))}
                </ul>
                <form onSubmit={handleSubmitComment}>
                    <input type="text" value={commentText} onChange={(e) => setCommentText(e.target.value)} />
                    <button type="submit">Add Comment</button>
                </form>
            </div>
        </div>
    );
};

export default PostPage;
