import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom';
import ReadPosts from './pages/ReadPosts';
import CreatePost from './pages/CreatePost';
import EditPost from './pages/EditPost';
import { Link } from 'react-router-dom';
import DetailPost from './pages/DetailPost';

const App = () => {
  const posts = [
    {
      id: '1',
      title: 'Cartwheel in Chelsea ü§∏üèΩ‚Äç‚ôÄÔ∏è',
      author: 'Harvey Milian',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-27T12:30:45Z', // Example timestamp
    },
    {
      id: '2',
      title: 'Love Lock in Paris üîí',
      author: 'Beauford Delaney',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-26T09:15:30Z', // Example timestamp
    },
    {
      id: '3',
      title: 'Wear Pink on Fridays üéÄ',
      author: 'Onika Tonya',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-25T15:45:20Z', // Example timestamp
    },
    {
      id: '4',
      title: 'Adopt a Dog üê∂',
      author: 'Denise Michelle',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-24T18:20:10Z', // Example timestamp
    },
  ];

  // Sets up routes
  let element = useRoutes([
    {
      path: '/',
      element: <ReadPosts data={posts} className="ReadPosts" />,
    },
    {
      path: '/edit/:id',
      element: <EditPost data={posts} />,
    },
    {
      path: '/new',
      element: <CreatePost />,
    },
    // Define route for detail view page
    {
      path: '/detail/:id',
      element: <DetailPost data={posts} />,
    },
  ]);

  return (
    <div className="App">
      <div className="header">
        <h1 className="title">Pug Post</h1>
        <Link to="/">
          <button className="headerBtn"> Explore Page </button>
        </Link>
        <Link to="/new">
          <button className="headerBtn"> Create Post </button>
        </Link>
      </div>
      {element}
    </div>
  );
};

export default App;
