import React, { useState, useEffect } from 'react';
import './Card.css';
import more from './more.png';
import { Link } from 'react-router-dom';
import { supabase } from '../client'; // Import the supabase client

const Card = (props) => {
    const [count, setCount] = useState(props.likes || 0); // Initialize count with likes if available

    const updateCount = async () => {
        const updatedCount = count + 1;
        setCount(updatedCount);

        // Update the bet count in the database
        try {
            const { data, error } = await supabase
                .from('Posts')
                .update({ betCount: updatedCount }) // Update the bet count
                .eq('id', props.id); // Update the post with the matching id

            if (error) {
                throw error;
            }

            // Store the updated count in local storage
            localStorage.setItem(`betCount_${props.id}`, updatedCount.toString());
        } catch (error) {
            console.error('Error updating bet count:', error.message);
            // Rollback the count if there's an error
            setCount(count);
        }
    };

    useEffect(() => {
        // Fetch the current count from the database when component mounts
        const fetchData = async () => {
            try {
                const { data, error } = await supabase
                    .from('Posts')
                    .select('betCount')
                    .eq('id', props.id)
                    .single();

                if (error) {
                    throw error;
                }

                // Update count with the fetched value
                setCount(data.betCount || 0);
            } catch (error) {
                console.error('Error fetching bet count:', error.message);
            }
        };

        fetchData(); // Call the async function
    }, [props.id]); // Fetch data when the id prop changes

    return (
        <div className="Card">
            <Link to={`/post/${props.id}`}>
                <img className="moreButton" alt="edit button" src={more} />
            </Link>
            <h2 className="title">{props.title}</h2>
            <h3 className="author">{"by " + props.author}</h3>
            {props.image && <img className="image" src={props.image} alt="post" />}
            <p className="description">{props.description}</p>
            {props.created_at && (
                <p className="timestamp">Created at: {new Date(props.created_at).toLocaleString()}</p>
            )}
            <button className="betButton" onClick={updateCount}>
                &#128077; Likes: {count}
            </button>
        </div>
    );
};

export default Card;
