import React, { useState } from 'react';
import './CreatePost.css';
import { supabase } from '../client';

const CreatePost = () => {
    const [post, setPost] = useState({ title: '', author: '', description: '', image: '' });

    const handleChange = (event) => {
        setPost({ ...post, [event.target.name]: event.target.value });
    };

    const handleImageUpload = async (event) => {
        const file = event.target.files[0];
        const fileData = await readFile(file);
        setPost({ ...post, image: fileData });
    };

    const createPost = async (event) => {
        event.preventDefault();

        await supabase
            .from('Posts')
            .insert({ title: post.title, author: post.author, description: post.description, image: post.image })
            .select();

        window.location = "/";
    };

    // Helper function to read file as Data URL
    const readFile = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onloadend = () => {
                resolve(reader.result);
            };
            reader.onerror = reject;
            reader.readAsDataURL(file);
        });
    };

    return (
        <div>
            <form>
                {/* Existing form fields */}
                <label htmlFor="image">Image</label><br />
                <input type="file" id="image" name="image" onChange={handleImageUpload} /><br />
                <br />
                <input type="submit" value="Submit" onClick={createPost} />
            </form>
        </div>
    );
};

export default CreatePost;
