import React, { useState } from 'react';
import './CreatePost.css';
import { supabase } from '../client';

const CreatePost = () => {
    const [post, setPost] = useState({ title: '', author: '', description: '', image: null });

    const handleChange = (event) => {
        if (event.target.name === 'image') {
            setPost({ ...post, image: event.target.files[0] });
        } else {
            setPost({ ...post, [event.target.name]: event.target.value });
        }
    };

    const createPost = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('title', post.title);
        formData.append('author', post.author);
        formData.append('description', post.description);
        formData.append('image', post.image); // Append image file

        await supabase
            .from('Posts')
            .insert(formData)
            .select();

        window.location = "/";
    };

    return (
        <div>
            <form>
                <label htmlFor="title">Title</label> <br />
                <input type="text" id="title" name="title" value={post.title} onChange={handleChange} /><br />
                <br />

                <label htmlFor="author">Author</label><br />
                <input type="text" id="author" name="author" value={post.author} onChange={handleChange} /><br />
                <br />

                <label htmlFor="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" name="description" value={post.description} onChange={handleChange}></textarea>
                <br />

                {/* Input field for image upload */}
                <label htmlFor="image">Image</label><br />
                <input type="file" id="image" name="image" onChange={handleChange} /><br />
                <br />

                <input type="submit" value="Submit" onClick={createPost} />
            </form>
        </div>
    );
};

export default CreatePost;
