import React from 'react';
import { useRoutes, Link } from 'react-router-dom';
import styled from 'styled-components';
import ReadPosts from './pages/ReadPosts';
import CreatePost from './pages/CreatePost';
import EditPost from './pages/EditPost';
import PostPage from './pages/PostPage'; // Import the PostPage component

const App = () => {
  const posts = [
    {
      id: '1',
      title: 'Cartwheel in Chelsea ü§∏üèΩ‚Äç‚ôÄÔ∏è',
      author: 'Harvey Milian',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-27T12:30:45Z',
    },
    {
      id: '2',
      title: 'Love Lock in Paris üîí',
      author: 'Beauford Delaney',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-26T09:15:30Z',
    },
    {
      id: '3',
      title: 'Wear Pink on Fridays üéÄ',
      author: 'Onika Tonya',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-25T15:45:20Z',
    },
    {
      id: '4',
      title: 'Adopt a Dog üê∂',
      author: 'Denise Michelle',
      description:
        'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      created_at: '2024-04-24T18:20:10Z',
    },
  ];

  let element = useRoutes([
    {
      path: '/',
      element: <ReadPosts data={posts} />,
    },
    {
      path: '/edit/:id',
      element: <EditPost data={posts} />,
    },
    {
      path: '/new',
      element: <CreatePost />,
    },
    {
      path: '/post/:id',
      element: <PostPage />,
    },
  ]);

  return (
    <AppContainer>
      <Header>
        <Link to="/" className="titleLink">
          <Title>Pug Post</Title>
        </Link>
        <Button to="/">Explore Page</Button>
        <Button to="/new">Create Post</Button>
      </Header>
      {element}
    </AppContainer>
  );
};

const AppContainer = styled.div`
  font-family: 'Avenir', 'Century Gothic', sans-serif;
  font-weight: 400;
  font-size: calc(10px + 2vmin);
  color: #fff;
  background: linear-gradient(135deg, rgba(44, 62, 80, 0.8), rgba(44, 62, 80, 0.6)), #2c3e50;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Header = styled.div`
  width: 100%;
  text-align: center;
  margin-bottom: 20px;
`;

const Title = styled.h1`
  color: #0b1992;
  font-size: 48px;
  font-weight: bold;
  margin-bottom: 20px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
`;

const Button = styled(Link)`
  display: inline-block;
  margin: 10px;
  background-color: #3498db;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  font-weight: bold;
  font-family: 'Avenir', 'Century Gothic', sans-serif;
  color: #fff;
  text-decoration: none;
  border-radius: 25px;
  transition: background-color 0.3s ease, transform 0.2s ease;

  &:hover {
    background-color: #2980b9;
    transform: translateY(-2px);
  }
`;

export default App;
